import React , { useState } from 'react';
import { Link } from 'react-router-dom'
import PageDefault from '../../../componentes/PageDefault/pageDefaultIndex'

function CadastroCategoria() {
  const [categorias, setCategorias] = useState([]);
  const [nomeDaCategoria, setNomeDaCategoria] = useState("Filmes") ;
  console.log(nomeDaCategoria)

    return(
        <PageDefault>
        <h1>Cadastro de Categoria: {nomeDaCategoria} </h1>

        <form onSubmit={function handleSubimit(infosEvento) {
            infosEvento.preventDefault();
            console.log('enviar form', infosEvento);
            setCategorias([ 
                ...categorias,
                nomeDaCategoria

              ]);
        }} >
          <div>  
          <label>
            Nome da Categoria:
            <input
              type="text"
              value={nomeDaCategoria}
              onChange={ 
                function handlerQueOErroPediu(infosEvento){
                  console.log('[nome categoria]', nomeDaCategoria);
                  console.log('[nome catgoria . target . value]', infosEvento.target.value);
                  setNomeDaCategoria(infosEvento.target.value);
                }
               }
            />
          </label>
          </div>

          <button>
            Cadastrar
          </button>
        </form>

               <ul>
                 {categorias.map((categoria, index) => {
                   return(
                     <li key={`${categoria}${index}`}>
                       {categoria}
                     </li>
                   )
                 })}
               </ul>


        <Link to="/">
          Ir para home
        </Link>
      </PageDefault>
    )
}

export default CadastroCategoria;
