import React, { useState } from 'react';
import { Link } from 'react-router-dom'
import PageDefault from '../../../componentes/PageDefault/pageDefaultIndex'

function CadastroCategoria() {
  const [categorias, setCategorias] = useState([]);

  const valoresIniciais = {
    nome: 'Categoria Incial',
    descricao: 'descrição inicial',
    cor: '#141414',
  }
  const [values, setValues] = useState(valoresIniciais);

    function setValue(chave, valor) {
  //chave: nome, descrição
      setValues({
        ...values,
        [chave]: valor, //nome: 'valor' o que vem na funcao setValue
      })
    }

  return (
    <PageDefault>
      <h1>Cadastro de Categoria: {values.nome} </h1>

      <form onSubmit={function handleSubimit(infosDoEvento) {
        infosDoEvento.preventDefault();
        console.log('enviar form', infosDoEvento);
        setCategorias([
          ...categorias,
          setValue
        ]);
      }} >
        <div>
          <label>
            Nome da Categoria:
            <input
              type="text"
              value={values.nome}
              onChange={
                function handlerQueOErroPediu(infosDoEvento) {
                  setValue(infosDoEvento.target.value);
                }
              }
            />
          </label>
        </div>


        <div>
          <label>
            Descrição:
            <input
              type="text"
              value={values.descricao}
              onChange={
                function handlerQueOErroPediu(infosDoEvento) {
                  setValue(infosDoEvento.target.value);
                }
              }
            />
          </label>
        </div>

        <div>
          <label>
            Cor:
            <input
              type="color"
              value={values.cor}
              onChange={
                function handlerQueOErroPediu(infosDoEvento) {
                  setValue(infosDoEvento.target.value);
                }
              }
            />
          </label>
        </div>

        <button>
          Cadastrar
          </button>
      </form>

      <ul>
        {categorias.map((categoria, index) => {
          return (
            <li key={`${categoria}${index}`}>
              {categoria}
            </li>
          )
        })}
      </ul>


      <Link to="/">
        Ir para home
        </Link>
    </PageDefault>
  )
}

export default CadastroCategoria;
