import React, { useState } from 'react';
import { Link } from 'react-router-dom'
import PageDefault from '../../../componentes/PageDefault/pageDefaultIndex'

function CadastroCategoria() {
  const [categorias, setCategorias] = useState([]);

  const valoresIniciais = {
    nome: 'Categoria Incial',
    descricao: 'descrição inicial',
    cor: '#141414',
  }
  const [values, setValues] = useState(valoresIniciais);

  function setValue(chave, valor) {
//chave: nome, descrição

    setValues({
      ...values,
      [chave]: valor, //nome: valor
    })
  }

  return (
    <PageDefault>
      <h1>Cadastro de Categoria: {nomeDaCategoria} </h1>

      <form onSubmit={function handleSubimit(infosDoEvento) {
        infosDoEvento.preventDefault();
        console.log('enviar form', infosDoEvento);
        setCategorias([
          ...categorias,
          nomeDaCategoria

        ]);
      }} >
        <div>
          <label>
            Nome da Categoria:
            <input
              type="text"
              value={values.nome}
              onChange={
                function handlerQueOErroPediu(infosDoEvento) {
                  setValue(infosDoEvento.target.value);
                }
              }
            />
          </label>
        </div>


        <div>
          <label>
            Descrição:
            <input
              type="text"
              value={nomeDaCategoria}
              onChange={
                function handlerQueOErroPediu(infosDoEvento) {
                  console.log('[nome categoria]', nomeDaCategoria);
                  console.log('[nome catgoria . target . value]', infosDoEvento.target.value);
                  setNomeDaCategoria(infosDoEvento.target.value);
                }
              }
            />
          </label>
        </div>

        <div>
          <label>
            Cor:
            <input
              type="color"
              value={nomeDaCategoria}
              onChange={
                function handlerQueOErroPediu(infosDoEvento) {
                  console.log('[nome categoria]', nomeDaCategoria);
                  console.log('[nome catgoria . target . value]', infosDoEvento.target.value);
                  setNomeDaCategoria(infosDoEvento.target.value);
                }
              }
            />
          </label>
        </div>

        <button>
          Cadastrar
          </button>
      </form>

      <ul>
        {categorias.map((categoria, index) => {
          return (
            <li key={`${categoria}${index}`}>
              {categoria}
            </li>
          )
        })}
      </ul>


      <Link to="/">
        Ir para home
        </Link>
    </PageDefault>
  )
}

export default CadastroCategoria;
